import { LitElement, html } from "lit-element";

const BaseURL = "http://localhost:8080";
export class GameMain extends LitElement {
  constructor() {
    super();
    // 0: menu, 1: game ,2: End
    this.page = 0;

    this.game_setting = {
      type: 0,
      runningTime: 0,
      level: 0,
    };
    this.problem = [];
    this.history = [];
    this.index = 0;
    this.semple = {
      Problem: "",
      Answer: 0,
      User_Answer: 0,
      Time: 0,
      Ok: false,
    };
  }
  createRenderRoot() {
    return this;
  }
  async startClick() {
    const ri = document.querySelector('input[name="ri"]:checked');
    const le = document.querySelector('input[name="le"]:checked');
    const ti = document.querySelector('input[name="ti"]:checked');

    if (ri == null || le == null || ti == null) {
      let temp = document.createElement("div");
      temp.innerText = "선택좀;;";
      temp.style.color = "red";
      temp.style.fontSize = "2rem";
      temp.id = "error";
      document.getElementsByClassName("container")[0].appendChild(temp);
      return;
    } else {
      try {
        let temp = document.getElementById("error");
        temp.remove();
      } catch (e) {}
    }
    this.game_setting = {
      type: ri.value,
      runningTime: ti.value,
      level: le.value,
    };
    let data = await axios({
      url: BaseURL + "/game/create",
      method: "post",
      headers: {
        Cookie: "token=" + localStorage.getItem("token"),
      },
      body: this.game_setting,
    });
    console.log(data);
    this.page = 1;
    this.requestUpdate();
  }
  setting() {
    return html`
      <div class="container">
        <fieldset>
          <label class="item">
            <input
              type="radio"
              name="ri"
              class="item_radio"
              id="huey"
              value="0"
            />
            <label for="huey">+</label>
          </label>
          <label class="item">
            <input
              type="radio"
              name="ri"
              class="item_radio"
              id="huey"
              value="1"
            />
            <label for="huey">-</label>
          </label>
          <label class="item">
            <input
              type="radio"
              name="ri"
              class="item_radio"
              id="huey"
              value="2"
            />
            <label for="huey">✕</label>
          </label>
          <label class="item">
            <input
              type="radio"
              name="ri"
              class="item_radio"
              id="huey"
              value="3"
            />
            <label for="huey">÷</label>
          </label>
          <label class="item">
            <input
              type="radio"
              name="ri"
              class="item_radio"
              id="huey"
              value="4"
            />
            <label for="huey">ALL</label>
          </label>
        </fieldset>

        <fieldset>
          <label class="item">
            <input
              type="radio"
              name="le"
              class="item_radio"
              id="huey"
              value="0"
            />
            <label for="huey">0~10</label>
          </label>
          <label class="item">
            <input
